import React, { Component } from 'react';
import './App.css';
import axios from 'axios';
import SearchBar from './components/searchbar'

class App extends Component {
  constructor(props){
    super(props);
    this.state={
      articles: [],
      term: null

    }
  }
componentWillMount(){
  this.wikiSearch('Bees');
}

wikiSearch(term){
  axios.get('https://en.wikipedia.org/w/api.php?action=query&titles='+ term +'&prop=revisions&rvlimit=10&rvprop=content&list=allpages&apfrom='+ term +'&aplimit=10&alprop=ids|title&format=json')
    .then((response) => {
      console.log(response)
      this.setState({articles:response.data.query.allpages}, function(){
        console.log(this.state);
      });
    })
}



  render() {
    return (
      <div className="App">
      <h1>Wikipedia Search</h1>
      </div>
    );
  }
}

export default App;
